import{_ as a,o as e,c as i,a as r}from"./app.10b9a472.js";var t="/rac-white-paper/images/4.png",d="/rac-white-paper/images/5.png";const l={},n=r('<h1 id="burydao\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#burydao\u7B80\u4ECB" aria-hidden="true">#</a> BuryDAO\u7B80\u4ECB</h1><p>BuryDAO\u662F\u5168\u7403\u7B2C\u4E00\u4E2A\u7ACB\u8DB3\u4E8E\u533A\u5757\u94FE\u6F6E\u724C\u9886\u57DF\u7684DAO\u5E73\u53F0\uFF0C\u5B83\u5F00\u8F9F\u4E86\u4E00\u4E2A\u5168\u65B0\u7684\u8D5B\u9053\u3002\u65F6\u5C1A\u4EA7\u4E1A\u662F\u533A\u5757\u94FE\u4EA7\u4E1A\u91CC\u4E00\u4E2A\u8574\u542B\u5343\u4EBF\u7EA7\u8D22\u5BCC\u4F53\u91CF\u7684\u8D85\u7EA7\u4EA7\u4E1A\u751F\u6001\u3002</p><p>BuryDAO\u662F\u7531\u6765\u81EA\u4E16\u754C\u5404\u5730\u7684\u533A\u5757\u94FE\u8D44\u6DF1\u73A9\u5BB6\u548C\u65F6\u5C1A\u7231\u597D\u8005\u5171\u540C\u53D1\u8D77\u7684\u81EA\u6CBB\u793E\u533A\uFF0C\u793E\u533A\u6210\u5458\u4E3A\u4E86\u5171\u540C\u7684\u4F7F\u547D\u4E00\u4E00\u5C06\u6D41\u91CF\u8F6C\u6362\u6210\u4EF7\u503C\u3002BuryDAO\u521D\u521B\u56E2\u961F\u6210\u5458\u5728\u533A\u5757\u94FE\u9886\u57DF\u6709\u4E94\u5E74\u4EE5\u4E0A\u7ECF\u5386\uFF0C\u53C2\u4E0E\u591A\u4E2A\u4F18\u8D28\u9879\u76EE\uFF0C\u5982UNI\u3001ICP\u3001ENS\u3001DYDX \u7B49\uFF0C\u62E5\u6709\u4E30\u5BCC\u7684\u884C\u4E1A\u7ECF\u9A8C\u548C\u4F18\u8D28\u8D44\u6E90\uFF0C\u7528\u6570\u636E\u548C\u6D41\u91CF\u6362\u53D6\u6570\u4EE5\u4EBF\u4E07\u7F8E\u91D1\u7684\u4EF7\u503C\u3002</p><p><img src="'+t+'" alt=""></p><h3 id="dao\u7684\u80CC\u666F\u4E0E\u7531\u6765" tabindex="-1"><a class="header-anchor" href="#dao\u7684\u80CC\u666F\u4E0E\u7531\u6765" aria-hidden="true">#</a> DAO\u7684\u80CC\u666F\u4E0E\u7531\u6765</h3><p>\u968F\u7740DeFi\u3001NFT\u3001\u5143\u5B87\u5B99\u7B49\u5E94\u7528\u7684\u843D\u5730\uFF0CDAO\u6A21\u5F0F\u7684\u4F18\u52BF\u5F97\u5230\u51F8\u663E\u3002DAO,\u5168\u79F0DecentralizedAutonomousOrganization,\u662F\u4E00\u79CD\u5168\u65B0\u7684\u4EBA\u7C7B\u7EC4\u7EC7\u534F\u540C\u65B9\u5F0F\uFF0C\u662F\u57FA\u4E8E\u533A\u5757\u94FE\u6838\u5FC3\u601D\u60F3\u7406\u5FF5\uFF08\u7531\u8FBE\u6210\u540C\u4E00\u4E2A\u5171\u8BC6\u7684\u7FA4\u4F53\u81EA\u53D1\u4EA7\u751F\u7684\u5171\u521B\u3001\u5171\u5EFA\u3001\u5171\u6CBB\u3001\u5171\u4EAB\u7684\u534F\u540C\u884C\u4E3A\uFF09\u884D\u751F\u51FA\u6765\u7684\u4E00\u79CD\u7EC4\u7EC7\u5F62\u6001\u3002\u662F\u533A\u5757\u94FE\u89E3\u51B3\u4E86\u4EBA\u4E0E\u4EBA\u4E4B\u95F4\u7684\u4FE1\u4EFB\u95EE\u9898\u4E4B\u540E\u7684\u9644\u5C5E\u4EA7\u7269\u3002DAO\u662F\u4E00\u79CD\u5C06\u7EC4\u7EC7\u7684\u7BA1\u7406\u548C\u8FD0\u8425\u89C4\u5219\u4EE5\u667A\u80FD\u5408\u7EA6\u7684\u5F62\u5F0FDAO\u5305\u62EC\u53BB\u4E2D\u5FC3\u5316\u81EA\u6CBB\u793E\u533A\uFF0C\u4E5F\u5305\u62EC\u53BB\u4E2D\u5FC3\u5316\u516C\u53F8\u6216\u5E73\u53F0\uFF0C\u5B83\u662F\u56F4\u7ED5\u4E00\u4E2A\u4F7F\u547D\u800C\u5EFA\u7ACB\u8D77\u6765\u7684\u56E2\u4F53\u3002\u672A\u6765\u7684DAO\u90FD\u4F1A\u8DDF\u4F20\u7EDF\u516C\u53F8\u4E00\u6837\u6709\u72EC\u7279\u7684\u7ECF\u8425\u8303\u56F4\u3001\u6218\u7565\u5B9A\u4F4D\u3001\u5546\u4E1A\u8BA1\u5212\u3001\u76C8\u5229\u6A21\u5F0F\u3001\u7BA1\u7406\u673A\u5236\u7B49\u3002\u636E\u4E0D\u5B8C\u5168\u7EDF\u8BA1\uFF0C\u5168\u7403\u7EA6\u670911\u4EBF\u4EBA\u4EE5\u81EA\u7531\u804C\u4E1A\u8005\u7684\u8EAB\u4EFD\u5728\u5DE5\u4F5C\u3002\u8FD1\u4E24\u5E74\u53D7\u5230\u5168\u7403\u75AB\u60C5\u7684\u6301\u7EED\u5F71\u54CD\uFF0C\u7EBF\u4E0A\u529E\u516C\u3001\u4E91\u7AEF\u534F\u4F5C\u7684\u5DE5\u4F5C\u65B9\u5F0F\u6B63\u5728\u8FC5\u901F\u666E\u53CA\uFF0C\u8FD9\u610F\u5473\u7740\u8BB8\u591A\u4EBA\u7684\u80FD\u529B\u53EA\u9700\u5728\u7F51\u4E0A\u4FBF\u53EF\u5B9E\u73B0\u4E0E\u4ED6\u4EBA\u534F\u4F5C\u4EE5\u53CA\u80FD\u529B\u53D8\u73B0\u3002\u4E00\u4E9B\u77E5\u540D\u7684\u9879\u76EE\u80CC\u540E\u4FBF\u662F\u4E00\u7FA4\u5206\u5E03\u5728\u5168\u7403\u5404\u5730\u7684\u6280\u672F\u5F00\u53D1\u8005\u5728\u7EBF\u4E0A\u8FBE\u6210\u5171\u8BC6\u540E\uFF0C\u5171\u540C\u5B8C\u6210\u7684\uFF0C\u540D\u526F\u5176\u5B9E\u5730\u4EE5DAO\u7684\u65B9\u5F0F\u8FD0\u884C\u3002</p><p>\u6B64\u5916\uFF0C\u73B0\u5728\u7684\u65B0\u4E00\u4EE3\u5E74\u8F7B\u4EBA\u5728\u804C\u573A\u4E0A\u4E5F\u66F4\u52A0\u91CD\u89C6\u72EC\u7ACB\u6027\u4E0E\u516C\u5E73\u6027\uFF0C\u66F4\u613F\u610F\u4E0E\u4EF7\u503C\u89C2\u4E00\u81F4\u7684\u4EBA\u5408\u4F5C\uFF0C\u800CDAO\u53EF\u4EE5\u5F25\u5408\u4ED6\u4EEC\u5728\u56F4\u7ED5\u540C\u4E00\u76EE\u6807\u594B\u6597\u65F6\u7684\u9E3F\u6C9F\uFF0C\u751A\u81F3\u4E00\u4E9B\u7BA1\u7406\u8005\u7684\u51B3\u7B56\u884C\u4E3A\u53EF\u4EE5\u88AB\u57FA\u4E8EDAO\u7684\u6295\u7968\u884C\u4E3A\u6240\u66FF\u4EE3\uFF0C\u4EE5\u6B64\u5E2E\u52A9\u4ED6\u4EEC\u83B7\u5F97\u66F4\u9AD8\u6548\u7684\u534F\u4F5C\u4E0E\u4FE1\u4EFB\u3002</p><h3 id="dao\u7684\u6838\u5FC3\u7279\u5F81\u4E0E\u4F18\u52BF" tabindex="-1"><a class="header-anchor" href="#dao\u7684\u6838\u5FC3\u7279\u5F81\u4E0E\u4F18\u52BF" aria-hidden="true">#</a> DAO\u7684\u6838\u5FC3\u7279\u5F81\u4E0E\u4F18\u52BF</h3><ul><li><p>1)\u5206\u5E03\u5F0F\u4E0E\u53BB\u4E2D\u5FC3\u5316(distributedanddecentralized)</p><p>DAO\u4E2D\u4E0D\u5B58\u5728\u4E2D\u5FC3\u8282\u70B9\u4EE5\u53CA\u5C42\u7EA7\u5316\u7684\u7BA1\u7406\u67B6\u6784\uFF0C\u5B83\u901A\u8FC7\u81EA\u4E0B\u800C\u4E0A\u7684\u7F51\u7EDC\u8282\u70B9\u4E4B\u95F4\u7684\u4EA4\u4E92\u3001\u7ADE\u4E89\u4E0E\u534F\u4F5C\u6765\u5B9E\u73B0\u7EC4\u7EC7\u76EE\u6807\u3002\u56E0\u6B64\uFF0CDAO\u4E2D\u8282\u70B9\u4E0E\u8282\u70B9\u4E4B\u95F4\u3001\u8282\u70B9\u4E0E\u7EC4\u7EC7\u4E4B\u95F4\u7684\u4E1A\u52A1\u5F80\u6765\u4E0D\u518D\u7531\u884C\u653F\u96B6\u5C5E\u5173\u7CFB\u6240\u51B3\u5B9A\uFF0C\u800C\u662F\u9075\u5FAA\u5E73\u7B49\u3001\u81EA\u613F\u3001\u4E92\u60E0\u3001\u4E92\u5229\u7684\u539F\u5219\uFF0C\u7531\u5F7C\u6B64\u7684\u8D44\u6E90\u7980\u8D4B\u3001\u4E92\u8865\u4F18\u52BF\u548C\u5229\u76CA\u5171\u8D62\u6240\u9A71\u52A8\u3002\u6BCF\u4E2A\u7EC4\u7EC7\u8282\u70B9\u90FD\u5C06\u6839\u636E\u81EA\u5DF1\u7684\u8D44\u6E90\u4F18\u52BF\u548C\u624D\u80FD\u8D44\u8D28\uFF0C\u5728\u901A\u8BC1\u7684\u6FC0\u52B1\u673A\u5236\u7684\u4F5C\u7528\u4E0B\u6709\u6548\u534F\u4F5C\uFF0C\u4ECE\u800C\u4EA7\u751F\u5F3A\u5927\u7684\u534F\u540C\u6548\u5E94\u3002</p></li><li><p>2)\u81EA\u4E3B\u6027\u4E0E\u81EA\u52A8\u5316(autonomousandautomated)</p><p>\u5728\u4E00\u4E2A\u7406\u60F3\u72B6\u6001\u7684DAO\u4E2D\uFF0C\u7BA1\u7406\u662F\u4EE3\u7801\u5316\u3001\u7A0B\u5E8F\u5316\u4E14\u81EA\u52A8\u5316\u7684\u3002\u201C\u4EE3\u7801\u5373\u6CD5\u5F8B\u201D(codeislaw),\u7EC4\u7EC7\u4E0D\u518D\u662F\u91D1\u5B57\u5854\u5F0F\u800C\u662F\u5206\u5E03\u5F0F\uFF0C\u6743\u529B\u4E0D\u518D\u662F\u4E2D\u5FC3\u5316\u800C\u662F\u53BB\u4E2D\u5FC3\u5316\uFF0C\u7BA1\u7406\u4E0D\u518D\u662F\u79D1\u5C42\u5236\u800C\u662F\u793E\u533A\u81EA\u6CBB\uFF0C\u7EC4\u7EC7\u8FD0\u884C\u4E0D\u518D\u9700\u8981\u516C\u53F8\u800C\u662F\u7531\u9AD8\u5EA6\u81EA\u6CBB\u7684\u793E\u533A\u6240\u66FF\u4EE3\u3002\u6B64\u5916\uFF0C\u7531\u4E8EDAO\u8FD0\u884C\u5728\u7531\u5229\u76CA\u76F8\u5173\u8005\u5171\u540C\u786E\u5B9A\u7684\u8FD0\u884C\u6807\u51C6\u548C\u534F\u4F5C\u6A21\u5F0F\u4E0B\uFF0C\u7EC4\u7EC7\u5185\u90E8\u7684\u5171\u8BC6\u548C\u4FE1\u4EFB\u66F4\u6613\u8FBE\u6210\uFF0C\u53EF\u4EE5\u6700\u5927\u9650\u5EA6\u5730\u964D\u4F4E\u7EC4\u7EC7\u7684\u4FE1\u4EFB\u6210\u672C\u3001\u6C9F\u901A\u6210\u672C\u548C\u4EA4\u6613\u6210\u672C\u3002</p></li><li><p>3)\u7EC4\u7EC7\u5316\u4E0E\u6709\u5E8F\u6027(organizedandordered)</p><p>\u4F9D\u8D56\u4E8E\u667A\u80FD\u5408\u7EA6\uFF0CDAO\u4E2D\u7684\u8FD0\u8F6C\u89C4\u5219\u3001\u53C2\u4E0E\u8005\u7684\u804C\u8D23\u6743\u5229\u4EE5\u53CA\u5956\u60E9\u673A\u5236\u7B49\u5747\u516C\u5F00\u900F\u660E\u3002\u6B64\u5916\uFF0C\u901A\u8FC7\u4E00\u7CFB\u5217\u9AD8\u6548\u7684\u81EA\u6CBB\u539F\u5219\uFF0C\u76F8\u5173\u53C2\u4E0E\u8005\u7684\u6743\u76CA\u5F97\u5230\u7CBE\u51C6\u5206\u5316\u4E0E\u964D\u7EF4\uFF0C\u5373\u7ED9\u90A3\u4E9B\u4ED8\u51FA\u52B3\u52A8\u3001\u505A\u51FA\u8D21\u732E\u3001\u627F\u62C5\u8D23\u4EFB\u7684\u4E2A\u4F53\u5339\u914D\u76F8\u5E94\u7684\u6743\u5229\u548C\u6536\u76CA\uFF0C\u4EE5\u4FC3\u8FDB\u4EA7\u4E1A\u5206\u5DE5\u4EE5\u53CA\u6743\u5229\u3001\u8D23\u4EFB\u3001\u5229\u76CA\u5747\u7B49\uFF0C\u4F7F\u5F97\u7EC4\u7EC7\u8FD0\u8F6C\u66F4\u52A0\u534F\u8C03\u3001\u6709\u5E8F\u3002</p></li><li><p>4)\u667A\u80FD\u5316\u4E0E\u901A\u8BC1\u5316(intelligenceandtokenization)</p><p>DAO\u5E95\u5C42\u4EE5\u5C01\u88C5\u4E86\u652F\u6301DAO\u53CA\u5176\u884D\u751F\u5E94\u7528\u7684\u6240\u6709\u57FA\u7840\u8BBE\u65BD\u4E00\u4E00\u4E92\u8054\u7F51\u57FA\u7840\u534F\u8BAE\u3001\u533A\u5757\u94FE\u6280\u672F\u3001\u4EBA\u5DE5\u667A\u80FD\u3001\u5927\u6570\u636E\u3001\u7269\u8054\u7F51\u7B49\u4E3A\u6280\u672F\u652F\u6491\uFF0C\u4EE5\u6570\u5B57\u5316\u3001\u667A\u80FD\u5316\u3001\u94FE\u4E0A\u94FE\u4E0B\u534F\u540C\u6CBB\u7406\u4E3A\u6CBB\u7406\u624B\u6BB5\uFF0C\u6539\u53D8\u4E86\u4F20\u7EDF\u7684\u79D1\u5C42\u5236\u4EE5\u53CA\u4EBA\u4E3A\u5F0F\u7BA1\u7406\u65B9\u5F0F\uFF0C\u5B9E\u73B0\u4E86\u7EC4\u7EC7\u7684\u667A\u80FD\u5316\u7BA1\u7406\u3002\u901A\u8BC1(token)\u4F5C\u4E3ADAO\u6CBB\u7406\u8FC7\u7A0B\u4E2D\u7684\u91CD\u8981\u6FC0\u52B1\u624B\u6BB5\uFF0C\u5C06\u7EC4\u7EC7\u4E2D\u7684\u5404\u4E2A\u5143\u7D20(\u4F8B\u5982\u4EBA\u3001\u7EC4\u7EC7\u3001\u77E5\u8BC6\u3001\u4E8B\u4EF6\u3001\u4EA7\u54C1\u7B49)\u6570\u5B57\u5316\u3001\u901A\u8BC1\u5316\uFF0C\u4ECE\u800C\u4F7F\u5F97\u8D27\u5E01\u8D44\u672C\u3001\u4EBA\u529B\u8D44\u672C\u4EE5\u53CA\u5176\u4ED6\u8981\u7D20\u8D44\u672C\u5145\u5206\u878D\u5408\uFF0C\u66F4\u597D\u5730\u6FC0\u53D1\u7EC4\u7EC7\u7684\u6548\u80FD\u548C\u5B9E\u73B0\u4EF7\u503C\u6D41\u8F6C\u3002</p></li></ul><h3 id="\u5E73\u53F0\u6587\u5316" tabindex="-1"><a class="header-anchor" href="#\u5E73\u53F0\u6587\u5316" aria-hidden="true">#</a> \u5E73\u53F0\u6587\u5316</h3><p>\u6587\u5316\u662F\u4E00\u4E2A\u7EC4\u7EC7\u7684\u7075\u9B42\uFF0C\u4E5F\u662F\u4E00\u4E2A\u7EC4\u7EC7\u80FD\u591F\u957F\u4E45\u4E0D\u8870\u7684\u57FA\u77F3\uFF0C\u6587\u5316\u5BF9\u4E8E\u53BB\u4E2D\u5FC3\u5316\u81EA\u6CBB\u7EC4\u7EC7</p><ul><li>DAO\u66F4\u4E3A\u91CD\u8981\u3002BuryDAO\u4E5F\u6709\u7740\u72EC\u7279\u7684\u5E73\u53F0\u6587\u5316\uFF1A <ul><li>\u613F\u666F\uFF1A\u6784\u5EFA\u4E00\u4E2A\u9AD8\u8BA4\u77E5\u3001\u9AD8\u5171\u8BC6\u3001\u9AD8\u81EA\u6CBB\u7684\u9876\u7EA7\u5708\u5C42</li><li>\u76EE\u6807\uFF1A\u6253\u9020\u5343\u4EBF\u7EA7\u533A\u5757\u94FE\u65F6\u5C1A\u4EA7\u4E1A\u751F\u6001</li><li>\u7406\u5FF5\uFF1A\u7528\u6709\u9650\u7684\u6570\u636E\u521B\u9020\u65E0\u9650\u7684\u8D22\u5BCC</li><li>\u4F7F\u547D\uFF1A\u8BA9\u6BCF\u4E00\u4E2ADAO\u6210\u5458\u5B9E\u73B0\u4EF7\u503C\u548C\u68A6\u60F3</li><li>\u4EF7\u503C\u89C2\uFF1A\u4E13\u4E1A\u3001\u4E13\u6CE8\u3001\u534F\u4F5C\u3001\u5171\u8D62</li></ul></li></ul><p><img src="'+d+'" alt=""></p>',13),o=[n];function p(c,h){return e(),i("div",null,o)}var u=a(l,[["render",p],["__file","1.introduction.html.vue"]]);export{u as default};
