import{_ as e,o as a,c as r,a as o}from"./app.dda6c996.js";const i={},t=o(`<h1 id="\u751F\u6001\u62D3\u5C55" tabindex="-1"><a class="header-anchor" href="#\u751F\u6001\u62D3\u5C55" aria-hidden="true">#</a> \u751F\u6001\u62D3\u5C55</h1><p>BuryDAO\u7684\u6574\u4E2A\u4EA7\u4E1A\u751F\u6001\u5305\u62EC\u4E94\u5927\u677F\u5757\uFF0CBuryDAO\u901A\u8BC1\u5C06\u8D2F\u7A7F\u6240\u6709\u4EA7\u4E1A\u677F\u5757\uFF1A</p><h3 id="_1-\u5F00\u521B\u6FC0\u52B1\u673A\u5236-\u6536\u5165\u6A21\u5F0F\u7684defi3-0\u65B0\u65F6\u4EE3" tabindex="-1"><a class="header-anchor" href="#_1-\u5F00\u521B\u6FC0\u52B1\u673A\u5236-\u6536\u5165\u6A21\u5F0F\u7684defi3-0\u65B0\u65F6\u4EE3" aria-hidden="true">#</a> 1. \u5F00\u521B\u6FC0\u52B1\u673A\u5236+\u6536\u5165\u6A21\u5F0F\u7684DeFi3.0\u65B0\u65F6\u4EE3</h3><p>\u53C2\u4E0E\u7B97\u529B\u6316\u77FF\u7684\u540C\u65F6\uFF0C\u514D\u8D39\u83B7\u5F97\u4F18\u8D28\u9879\u76EE\u7A7A\u6295\u8D44\u4EA7\u3002</p><blockquote><p>BuryDAO\u5E0C\u671B\u6210\u4E3Aweb3.0\u7684\u57FA\u7840\u8BBE\u65BD\uFF0C\u6784\u5EFA\u57FA\u4E8EIPFS\u534F\u8BAE\u7684\u5206\u5E03\u5F0F\u5B58\u50A8+DeFi \u751F\u6001\u3001\u8BA1\u7B97\u548C\u5E26\u5BBD\u6FC0\u52B1\u7F51\u7EDC\uFF0C\u6574\u5408\u5168\u4E16\u754C\u4E92\u8054\u7F51\u57FA\u7840\u8BBE\u65BD\u8D44\u6E90\uFF0C\u5E76\u5B9E\u73B0\u8D44\u6E90\u7684\u6700\u4F18\u5316\u5229\u7528\u3002</p></blockquote><blockquote><p>BuryDAO\u5B9E\u73B0\u4E86\u4E00\u5957\u66F4\u52A0\u5408\u7406\u7684\u5206\u5E03\u5F0F\u5B58\u50A8\u6FC0\u52B1\u673A\u5236\uFF0C\u5E76\u5C06\u9010\u6E10\u4ECE\u5206\u5E03\u5F0F\u5B58\u50A8\u8FDB\u5316\u4E3A\u5206\u5E03\u5F0F\u4E92\u8054\u7F51\u7684\u6FC0\u52B1\u5C42\uFF0C\u5B9E\u73B0\u66F4\u52A0\u7CBE\u7EC6\u5316\u7684\u6FC0\u52B1\uFF0C\u5B9E\u73B0\u8BA1\u7B97\u3001\u5E26\u5BBD\u548C\u5B58\u50A8\u8D44\u6E90\u7684\u6700\u4F18\u5316\u5229\u7528\u3002</p></blockquote><blockquote><p>BuryDAO\u5B9E\u73B0\u4E86\u4E00\u5957\u66F4\u597D\u7684\u5206\u5E03\u5F0F\u5B58\u50A8\u7F51\u7EDC\u6FC0\u52B1\u5C42:\u901A\u8FC7\u878D\u5408DeFI\u751F\u6001\u3001\u53D6\u6D88\u524D\u7F6E\u62B5\u62BC\u3001\u91C7\u7528\u65B0\u54C8\u5E0C\u51FD\u6570\u7B49\u6539\u8FDB\uFF0C\u5168\u9762\u964D\u4F4E\u4E86\u77FF\u5DE5\u53C2\u4E0E\u6316\u77FF\u7684\u2ED4\u69DB\uFF1B\u7ED3\u5408\u9012\u5F52\u96F6\u77E5\u8BC6\u8BC1\u660E\uFF0CPoST+VRF\u7B49\u6280\u672F\u521B\u65B0\uFF0C\u89E3\u51B3\u4E86TPS\u548C\u6D88\u606F\u4E0A\u94FE\u95EE\u9898\uFF0C\u6781\u5927\u7684\u63D0\u641E\u4E86JHE\u7F51\u7EDC\u7684\u53EF\u6269\u5C55\u6027\uFF1B\u5BF9\u6316\u77FF\u8F6F\u4EF6\u7684\u4F18\u5316\u4F7F\u6316\u77FF\u6548\u7387\u663E\u8457\u63D0\u5347\uFF0C\u63D0\u9AD8\u4E86\u7F51\u7EDC\u4E2D\u786C\u4EF6\u8D44\u6E90\u7684\u5229\u7528\u6548\u7387\u3002</p></blockquote><blockquote><p>BuryDAO\u91C7\u7528\u4E86\u66F4\u52A0\u516C\u5E73\u7684\u4EE3\u5E01\u5206\u53D1\u89C4\u5219\u4EE5\u53CA\u53BB\u4E2D\u5FC3\u5316\u793E\u533A\u6CBB\u7406\uFF0C\u6781\u5927\u5730\u63D0\u5347\u4E86\u534F\u8BAE\u7684\u516C\u5E73\u6027\uFF0C\u6709\u52A9\u4E8E\u9879\u76EE\u7684\u2ED3\u8FDC\u53D1\u5C55\uFF0C\u5E76\u6700\u7EC8\u5B9E\u73B0\u57FA\u4E8EIPFS\u534F\u8BAE\u7684\u5206\u5E03\u5F0F\u5B58\u50A8+DeFi\u751F\u6001\u3001\u8BA1\u7B97\u548C\u5E26\u5BBD\u6FC0\u52B1\u7F51\u7EDC\uFF0C\u6210\u4E3A\u672A\u6765web3.0\u57FA\u7840\u8BBE\u65BD\u7684\u91CD\u8981\u7EC4\u6210\u90E8\u5206\u3002</p></blockquote><blockquote><p>BuryDAO\u5C06\u5728\u5206\u5E03\u5F0F\u5B58\u50A8\u7684\u57FA\u7840\u4E0A\uFF0C\u6FC0\u52B1\u5927\u5BB6\u63D0\u4F9B\u53EF\u9A8C\u8BC1\u7684\u8BA1\u7B97\u548C\u53EF\u5EA6\u91CF\u7684\u5E26\u5BBD\u8D44\u6E90\u3002\u6211\u4EEC\u76F8\u4FE1\u968F\u7740\u533A\u5757\u94FE\u884C\u4E1A\u7684\u6574\u4F53\u53D1\u5C55\u548CBuryDAO\u56E2\u961F\u7684\u5168\u529B\u5F00\u53D1\uFF0C\u4E00\u4E2A\u5206\u5E03\u5F0F\u4E92\u8054\u7F51\u7684\u4E16\u754C\u6B63\u5728\u6211\u4EEC\u773C\u524D\u6162\u6162\u5C55\u5F00\u3002</p></blockquote><h3 id="_2-dao\u81EA\u6CBB\u4E0Enft" tabindex="-1"><a class="header-anchor" href="#_2-dao\u81EA\u6CBB\u4E0Enft" aria-hidden="true">#</a> 2. DAO\u81EA\u6CBB\u4E0ENFT</h3><p>\u901A\u8FC7NFT\u5B9E\u73B0\u53C2\u4E0E\u8005\u5171\u5EFA\u3001\u5171\u6CBB\u3001\u5171\u4EAB\uFF0C\u5E76\u5BF9\u751F\u6001\u8D21\u732E\u8005\u4E0D\u65AD\u7A7A\u6295NFT\u8D44\u4EA7\u3002</p><pre><code>NFT\u5E94\u7528\u793A\u4F8B\u7406\u8BBA\u4E0A\u8BF4\uFF0CNFT\u53EF\u4EE5\u5E94\u7528\u4E8E\u4EFB\u4F55\u9700\u8981\u8FDB\u884C\u552F\u4E00\u8BA4\u8BC1\u7684\u9886\u57DF\uFF0C\u5305\u62EC\u827A\u672F\u54C1\u3001\u6E38\u620F\u3001\u4EA7\u6743\u8BA4\u8BC1\u7B49\u8BF8\u591A\u65B9\u9762\uFF1A
</code></pre><ul><li>\u827A\u672F\uFF1ANFT\u5BF9\u827A\u672F\u4F5C\u54C1\u6240\u6709\u6743\u7684\u4FDD\u62A4\u6210\u4E3A\u5F00\u6E90\u548C\u521B\u9020\u7684\u827A\u672F\u539F\u52A8\u529B\u3002NFT\u5BF9\u4E8E\u5F88\u591A\u827A\u672F\u5BB6\uFF0C\u5C24\u5176\u662F\u6570\u5B57\u827A\u672F\u9886\u57DF\u7684\u827A\u672F\u5BB6\u800C\u8A00\uFF0C\u6700\u5927\u7684\u5229\u597D\u4E4B\u5904\u662F\u5B83\u63D0\u4F9B\u4E86\u5BF9\u827A\u672F\u4F5C\u54C1\u6240\u6709\u6743\u7684\u4FDD\u62A4\uFF0C\u5E76\u5728\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u4E3A\u6570\u5B57\u827A\u672F\u521B\u4F5C\u8005\u901A\u8FC7\u4F5C\u54C1\u83B7\u5F97\u5E94\u6709\u7684\u7ECF\u6D4E\u56DE\u62A5\u63D0\u4F9B\u65B0\u7684\u8DEF\u5F84\u3002</li><li>\u6E38\u620F\uFF1ANFT\u7684\u6700\u6D41\u884C\u7684\u5E94\u7528\u4E4B\u4E00\u3002NFT\u4E3A\u6E38\u620F\u8D44\u4EA7\u7684\u6570\u5B57\u6240\u6709\u6743\u63D0\u4F9B\u4E86\u4E00\u79CD\u4F18\u79C0\u7684\u89E3\u51B3\u65B9\u6848\uFF0C\u4F7F\u73A9\u5BB6\u80FD\u591F\u5B89\u5168\u5730\u4EA4\u6613\u8D44\u4EA7\uFF0C\u751A\u81F3\u53EF\u4EE5\u81EA\u884C\u51B3\u5B9A\u6E38\u620F\u7684\u672A\u6765\u53D1\u5C55\u65B9\u5411\u3002\u5728\u76EE\u524D\u4E00\u4E9B\u9887\u53D7\u6B22\u8FCE\u7684NFT\u6E38\u620F\uFF0C\u73A9\u5BB6\u53EF\u4EE5\u8D2D\u4E70\u6570\u5B57\u571F\u5730\u5757\uFF0C\u8FD9\u4E9B\u6570\u5B57\u5757\u968F\u540E\u53EF\u4EE5\u8F6C\u552E\u6216\u7528\u4F5C\u6E38\u620F\u4E2D\u7684\u5E7F\u544A\u7A7A\u95F4\u3002</li><li>\u8BA4\u8BC1\uFF1ANFT\u62E5\u6709\u7279\u5B9A\u8D44\u4EA7\u7684\u72EC\u7279\u4FE1\u606F\uFF0C\u8FD9\u4F7F\u5176\u80FD\u591F\u66F4\u597D\u5730\u7528\u4E8E\u5728\u4EFB\u4F55\u533A\u5757\u94FE\u7F51\u7EDC\u4E0A\u6CE8\u518C\u7684\u6807\u8BC6\u3001\u8BB8\u53EF\u8BC1\u3001\u8D44\u683C\u548C\u8BA4\u8BC1\u7B49\uFF1B\u5728\u7BA1\u63A7\u8BF8\u5982\u75C5\u53F2\u3001\u4E2A\u4EBA\u8D44\u6599\u3001\u6559\u80B2\u7A0B\u5EA6\u3001\u5730\u5740\u7B49\u5176\u4ED6\u6570\u5B57\u4FE1\u606F\u65F6\u4E5F\u5C06\u975E\u5E38\u6709\u7528\u3002</li><li>\u77E5\u8BC6\u4EA7\u6743\uFF1A\u5BF9\u4E8E\u4EFB\u4F55\u7684\u77E5\u8BC6\u4EA7\u6743\uFF0C\u5982\u6B4C\u66F2\u7248\u6743\u3001\u5F71\u89C6\u7248\u6743\u3001\u53D1\u660E\u4E13\u5229\u3001\u56FE\u7247\u7248\u6743\u3001\u7ED8\u753B\u7248\u6743\u7B49\uFF0C\u90FD\u53EF\u4EE5\u7528NFT\u8FDB\u884C\u8BA4\u8BC1\u3002\u7B80\u5355\u5730\u8BF4\uFF0C\u76F8\u5F53\u4E8E\u5728\u6BCF\u4E00\u4E2A\u4E1C\u897F\u540E\u9762\u8D34\u4E0A\u4E00\u4E2A\u65E0\u6CD5\u66F4\u6539\uFF0C\u4E14\u72EC\u4E00\u65E0\u4E8C\u7684\u6761\u5F62\u7801\uFF0C\u7528\u4E8E\u786E\u8BA4\u4EE5\u53CA\u8BC6\u522B\u8BE5\u9879\u8D44\u4EA7\u7684\u7248\u6743\u3002</li><li>\u623F\u5730\u4EA7\uFF1A\u73B0\u5B9E\u4E16\u754C\u4E2D\u7684\u623F\u5730\u4EA7\u8D44\u4EA7\u5728\u533A\u5757\u94FE\u4E0A\u88ABtoken\u5316\uFF0C\u8FD9\u6837\u53EF\u4EE5\u4F7F\u4EA4\u6613\u66F4\u4E3A\u987A\u7545\uFF0C\u6D88\u9664\u4E86\u7B2C\u4E09\u65B9\u4E2D\u4ECB\u673A\u6784\uFF0C\u8FD8\u80FD\u9632\u6B62\u4E86\u6240\u6709\u6743\u51B2\u7A81\u3002\u968F\u7740\u6280\u672F\u53CA\u8BA4\u77E5\u7684\u4E0D\u65AD\u53D1\u5C55\u4E0E\u8FDB\u6B65\uFF0CNFT\u7684\u5E94\u7528\u8FDC\u4E0D\u4EC5\u4EC5\u4E8E\u6B64\uFF0C\u76F8\u4FE1\u66F4\u591A\u6F5C\u529B\u53CA\u5E94\u7528\u573A\u666F\u5C06\u88AB\u6316\u6398\uFF0C\u672A\u6765\u53EF\u671F\u3002\u968F\u7740NFT\u7684\u6DF1\u5EA6\u53D1\u5C55\uFF0C\u4E3A\u5143\u5B87\u5B99\u6982\u5FF5\u7684\u8BDE\u751F\u548C\u6280\u672F\u843D\u5730\uFF0C\u63D0\u4F9B\u4E86\u4EF7\u503C\u6D41\u901A\u5F62\u5F0F\u548C\u5A92\u4ECB\u3002</li></ul><h3 id="_3-\u6559\u80B2\u4E0E\u8D44\u8BAF" tabindex="-1"><a class="header-anchor" href="#_3-\u6559\u80B2\u4E0E\u8D44\u8BAF" aria-hidden="true">#</a> 3. \u6559\u80B2\u4E0E\u8D44\u8BAF</h3><p>\u901A\u8FC7\u4E13\u4E1A\u6559\u80B2\u548C\u4F18\u8D28\u8D44\u8BAF\uFF0C\u57F9\u517B\u4E13\u4E1A\u3001\u4E13\u6CE8\u3001\u534F\u4F5C\u3001\u5171\u8D62\u7684\u533A\u5757\u94FE\u73A9\u5BB6\uFF0C\u6253\u9020\u4E00\u652F\u9AD8\u8BA4\u77E5\u3001\u9AD8\u5171\u8BC6\u3001\u9AD8\u81EA\u6CBB\u7684\u7C89\u4E1D\u94C1\u519B\u3002</p><h3 id="_4-\u65F6\u5C1A\u4EA7\u4E1A-\u8D26\u53F7\u4E0E\u8D44\u4EA7-\u4EA4\u6613\u5E02\u573A" tabindex="-1"><a class="header-anchor" href="#_4-\u65F6\u5C1A\u4EA7\u4E1A-\u8D26\u53F7\u4E0E\u8D44\u4EA7-\u4EA4\u6613\u5E02\u573A" aria-hidden="true">#</a> 4.\u65F6\u5C1A\u4EA7\u4E1A\uFF08\u8D26\u53F7\u4E0E\u8D44\u4EA7\uFF09\u4EA4\u6613\u5E02\u573A</h3><p>\u6253\u9020\u5168\u7403\u7B2C\u4E00\u4E2A\u65F6\u5C1A\u6F6E\u724C\u8D44\u4EA7\u4EA4\u6613\u5E02\u573A\u3002</p><h3 id="_5-\u4EA7\u4E1A\u5143\u5B87\u5B99" tabindex="-1"><a class="header-anchor" href="#_5-\u4EA7\u4E1A\u5143\u5B87\u5B99" aria-hidden="true">#</a> 5.\u4EA7\u4E1A\u5143\u5B87\u5B99</h3><p>\u6253\u9020\u4EA7\u4E1A\u5143\u5B87\u5B99\uFF0C30\u4E07\u539F\u8457\u5C45\u6C11\u548C\u4E0A\u767E\u4E07\u5165\u9A7B\u8005\uFF0C\u5171\u4EAB\u4EA7\u4E1A\u7EA2\u5229\u3002\u5143\u5B87\u5B99\u662F\u53BB\u4E2D\u5FC3\u5316\u548C\u5F00\u6E90\u865A\u62DF\u4E16\u754C\u4E2D\u7269\u7406\u4E16\u754C\u548C\u865A\u62DF\u4E16\u754C\u4E4B\u95F4\u7684\u6865\u6881\u3002\u5C06\u51FA \u73B0\u7528\u4E8E\u7BA1\u7406\u6570\u5B57\u7684\u52A0\u5BC6\u534F\u8BAE\u4EF7\u503C\uFF0C\u4F8B\u5982\u6570\u5B57\u827A\u672F\u3001\u623F\u5730\u4EA7\u548C\u6E38\u620F\u3002\u4FC3\u6210\u8FD9\u4E00\u5207\u7684\u63A8\u52A8\u8005\u5C06\u662FJHE\u7F51\u7EDC\u4E0A\u7684NFT\uFF0C\u5141\u8BB8\u8D1F\u62C5\u5F97\u8D77\u7684\u5FEB\u901F\u4EA4\u6613\u3002uryDAO\u5EFA\u7ACB\u5728Web3.0\u4E4B\u4E0A\uFF0C\u662F\u201C\u7A7F\u8D8A\u5F0F\u201D\u548C\u201C\u5206\u5E03\u5F0F\u201D\u7684\u533A\u5757\u94FE\u7F51\u7EDC\u3002\u5728\u8FD9\u6837\u7684\u7F51\u7EDC\u4E2D\uFF0C\u4EBA\u4EEC\u4E0D\u5149\u53EF\u4EE5\u8F93\u51FA\u6587\u5B57\u3001\u56FE\u7247\u30012D\u89C6\u9891\uFF0C\u8FD8\u80FD\u901A\u8FC7\u6C89\u6D78\u5F0F\u5F71\u50CF\uFF0C\u5B8C\u6210\u201C\u9762\u5BF9\u9762&quot;\u4E92\u52A8\u4F53\u9A8C\uFF0C\u4E5F\u5C31\u662F\u5728\u65B0\u7684\u4E92\u8054\u7F51\u91CC\u518D\u9020\u4E00\u4E2A\u201C\u771F\u5B9E\u201C\u7684\u201C\u865A\u62DF\u4E16\u754C\u201D\u3002</p><p>\u5728BuryDAO\u7F51\u7EDC\u91CC\uFF0C\u4EBA\u4EEC\u901A\u8FC7VR/AR\u8FD9\u6837\u7684\u865A\u62DF\u73B0\u5B9E\u6280\u672F\u5B8C\u6210\u751F\u6D3B\u4F53\u9A8C\uFF0C\u8FDB\u800C\u5851\u9020\u81EA\u5DF1\u7684\u865A\u62DF\u8EAB\u4EFD\uFF0C\u865A\u62DF\u793E\u7FA4\uFF0C\u5E76\u901A\u8FC7\u865A\u62DF\u4E16\u754C\uFF0C\u5B8C\u6210\u81EA\u5DF1\u7684\u7CBE\u795E\u751F\u6D3B\u3002\u751A\u81F3\uFF0C\u4EBA\u4EEC\u8FD8\u53EF\u4EE5\u901A\u8FC7\u5F00 \u53D1\u865A\u62DF\u623F\u4EA7\u3001\u865A\u62DF\u827A\u672F\u54C1\u3001\u6570\u5B57\u8D27\u5E01\u6765\u6784\u5EFA\u4E00\u6574\u5957\u7ECF\u6D4E\u7CFB\u7EDF\u3002</p><p>BuryDAO\u5C06\u5B9E\u73B0\u201C\u6BCF\u4E2A\u4E2A\u4F53\u3001\u65F6\u523B\u8054\u7F51\u3001\u5404\u53D6\u6240\u9700\u3001\u5B9E\u65F6\u4E92\u52A8\u201D\u7684\u72B6\u6001\uFF0C\u4E5F\u662F\u4E00\u4E2A\u201C\u4EE5\u4EBA\u4E3A\u672C\u201D\u7684\u4E92\u8054\u7F51\u601D\u7EF4\u6307\u5F15\u4E0B\u7684\u65B0\u5546\u4E1A\u6587\u660E\u65F6\u4EE3\u3002</p><p>BuryDAO\u672A\u6765\u66F4\u5B8C\u6574\u7684\u573A\u666F\uFF0C\u5C31\u662F\u4E00\u4E2A\u771F\u5B9E\u7684\u4EBA\uFF0C\u901A\u8FC7\u81EA\u5DF1\u7684\u6570\u5B57\u6A21\u578B\uFF0C\u5728\u6570\u5B57\u5DE5\u5382\u4E2D\u7A7F\u68AD\uFF0C\u5C31\u5B8C\u6210\u4E86\u771F\u5B9E\u5DE5\u5382\u4E2D\u7684\u751F\u4EA7\u64CD\u4F5C\u3002\u4F5C\u4E3A\u7387\u5148\u8FDB\u519B\u201C\u5143\u5B87\u5B99\u201D\u7684\u4E92\u8054\u7F51\u5DE8\u5934\uFF0C\u5FAE\u8F6F\u76EE\u524D\u5DF2 \u7ECF\u5BF9\u201C\u5546\u7528\u5143\u5B87\u5B99\u201D\u5E94\u7528\u505A\u4E86\u8BE6\u5C3D\u7684\u6280\u672F\u5206\u5C42\uFF0C\u5176\u4E2D\u6700\u5E95\u5C42\u5C31\u662F\u5305\u62EC\u4E86\u7269\u8054\u7F51\u548C\u6570\u5B57\u5B6A\u751F\u3002\u7269\u8054\u7F51\u9700\u8981\u66F4\u5FEB\u7684\u7F51\u7EDC\u3001\u66F4\u5C0F\u7684\u8BBE\u5907\u3001\u66F4\u8010\u7528\u7684\u7535\u6C60\u7B49\u7B49\uFF0C\u6070\u5982\u73B0\u4EE3\u793E\u4F1A\u5EFA\u7ACB\u5728\u57CE\u5E02\u9AD8\u697C\u3001 \u9053\u8DEF\u3001\u7535\u529B\u7B49\u57FA\u7840\u4E0A\uFF0C\u8FD9\u4E9B\u90FD\u662F\u4E00\u4E2A\u5168\u65B0\u7684\u5E9E\u5927\u6570\u5B57\u4E16\u754C\u5FC5\u4E0D\u53EF\u5C11\u7684\u57FA\u7840\u8BBE\u65BD\u3002\u5728\u57FA\u7840\u8BBE\u65BD\u4E4B\u4E0A\uFF0C\u201C\u6570\u5B57\u5B6A\u751F\u201D\u5C31\u53EF\u4EE5\u642D\u5EFA\u8D77\u6765\u4E86\uFF0C\u8FD9\u4E5F\u662F\u76EE\u524D\u4EBA\u4EEC\u5BF9\u5143\u5B87\u5B99\u6700\u5177\u4F53\u7684\u8BA4\u77E5\u3002</p><p>BuryDAO\u901A\u8FC7\u201C\u6570\u5B57\u5B6A\u751F\u201D\u6A21\u62DF\u51FA\u7684\u6570\u5B57\u4E16\u754C\uFF0C\u4EBA\u4EEC\u53EA\u9700\u5750\u5728\u5BB6\u91CC\uFF0C\u5C31\u53EF\u4EE5\u89C2\u6D4B\u548C\u63A7\u5236\u5343\u91CC\u4E4B\u5916\u7684\u5B9E\u7269\u72B6\u6001\u3002\u4E0D\u5149\u5982\u6B64\uFF0C\u5728\u6570\u5B57\u5B6A\u751F\u4F53\u91CC\uFF0C\u8FD8\u80FD\u5B8C\u6210\u4E00\u4E9B\u73B0\u5B9E\u4E16\u754C\u91CC\u65E0\u6CD5\u5B8C\u6210\u7684\u5B9E\u9A8C\u3002\u8FDB\u800C\uFF0C\u5F53\u6570\u5B57\u5B6A\u751F\u4F53\u88AB\u5582\u517B\u4E86\u8DB3\u591F\u591A\u7684\u73B0\u5B9E\u6570\u636E\uFF0C\u53C8\u80FD\u5B8C\u6210\u5B9E\u9A8C\u64CD\u4F5C\u4EE5\u540E\uFF0C\u5C31\u53EF\u4EE5\u6A21\u62DF\u8FD0\u884C\u8FC7\u53BB\u548C\u672A\u6765\u3002\u8FD9\u6837\uFF0C\u6570\u5B57\u5B6A\u751F\u4F53\u901A\u8FC7\u62E5\u6709\u8FC7\u53BB\u548C\u672A\u6765\u4EA7\u751F\u4E86\u65F6\u95F4\u548C\u53D8\u5316\u4E24\u4E2A\u7EF4\u5EA6\uFF0C\u4E5F\u8BB8\u5C31\u80FD\u8131\u79BB\u73B0\u5B9E\u800C\u5B58\u5728\u4E86\u3002</p><p>BuryDAO\u7684\u5143\u5B87\u5B99\u8FD8\u6709\u5176\u4ED6\u6280\u672F\uFF0C\u5982\u4EBA\u673A\u4E92\u52A8\u3001\u53BB\u4E2D\u5FC3\u5316\u7CFB\u7EDF\u3001\u7A7A\u95F4\u8BA1\u7B97\u80FD\u529B\u3002\u5728\u8FD9\u4E9B\u6280 \u672F\u7684\u57FA\u7840\u4E0A\uFF0C\u4EBA\u4EEC\u624D\u80FD\u5B9E\u73B0\u65B0\u7684\u521B\u9020\u548C\u53D1\u73B0\uFF0C\u8BA9\u666E\u901A\u4EBA\u4E5F\u771F\u7684\u8FDB\u5165\u201C\u5143\u5B87\u5B99\u201D\uFF0C\u62E5\u6709\u81EA\u5DF1\u7684\u6570\u5B57\u751F\u6D3B\u4F53\u9A8C\u3002</p>`,24),d=[t];function h(c,p){return a(),r("div",null,d)}var u=e(i,[["render",h],["__file","2.expansion.html.vue"]]);export{u as default};
