import{_ as a,o as e,c as i,a as l}from"./app.dda6c996.js";var r="/images/1.png",n="/images/2.png",d="/images/3.png";const h={},s=l('<h1 id="defi\u53D1\u5C55" tabindex="-1"><a class="header-anchor" href="#defi\u53D1\u5C55" aria-hidden="true">#</a> DEFI\u53D1\u5C55</h1><h3 id="_1-\u3001-defi\u7684\u5B9A\u4E49" tabindex="-1"><a class="header-anchor" href="#_1-\u3001-defi\u7684\u5B9A\u4E49" aria-hidden="true">#</a> 1)\u3001 Defi\u7684\u5B9A\u4E49</h3><p>DeFi \uFF0C \u5168 \u79F0 Decentralized Finance\uFF0C\u4E2D\u6587\u8BD1\u4E3A\u201C\u53BB\u4E2D\u5FC3\u5316\u91D1\u878D\u201D\u6216\u201C\u5F00\u653E\u91D1 \u878D\u201D\u3002\u76EE\u524D\u5E02\u573A\u70ED\u8BAE\u7684DeFi\u5B9E\u9645\u4E0A\u6307\u57FA\u4E8E\u4EE5\u592A\u574A\u7B49\u5E95\u5C42\u516C\u94FE\uFF0C\u5E76\u901A\u8FC7\u667A\u80FD\u5408\u7EA6\u6784\u5EFA \u7684\u53BB\u4E2D\u5FC3\u5316\u91D1\u878D\u534F\u8BAE\uFF08\u4EA7\u54C1/\u670D\u52A1\uFF09\u3002</p><p>DeFi\u57282018\u5E74\u5174\u8D77\uFF0C\u7ECF\u8FC7\u4E24\u5E74\u7684\u53D1\u5C55\uFF0C\u57282020\u5E74\u8FCE\u6765\u7206\u53D1\u3002\u76EE\u524D\uFF0CDeFi\u5DF2\u7ECF\u5F62\u6210 \u4E86\u8F83\u4E3A\u5B8C\u5584\u7684\u751F\u6001\u4F53\u7CFB\uFF0C\u6D89\u53CA\u652F\u4ED8\u3001\u501F\u8D37\u3001\u7A33\u5B9A\u5E01\u3001\u53BB\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240(DEX\uFF09\u3001\u884D\u751F \u54C1\u3001\u4FDD\u9669\u7B49\u3002</p><p><img src="'+r+'" alt="DeFi\u751F\u6001\u56FE\u8C31"></p><ul><li>\u533A\u5757\u94FE\u6280\u672F1.0\uFF1A\u6BD4\u7279\u5E01\u2014\u53BB\u4E2D\u5FC3\u5316\u3001\u6297\u901A\u80C0\u3001\u52A0\u5BC6\u7684\u5168\u7403\u6570\u5B57\u8D44\u4EA7</li><li>\u533A\u5757\u94FE\u6280\u672F2.0\uFF1A\u667A\u80FD\u5408\u7EA6\u2014\u57FA\u4E8E\u7F16\u7A0B\u8BED\u8A00\u8FD0\u884C\u5728\u53BB\u4E2D\u5FC3\u5316\u7F51\u7EDC\u4E0A\u7684\u4E0D\u53EF\u7BE1\u6539\u7684\u5408\u540C</li><li>\u533A\u5757\u94FE\u6280\u672F3.0\uFF1ADeFi\u2014\u57FA\u4E8E\u667A\u80FD\u5408\u7EA6\u53D1\u5C55\u7684\u53BB\u4E2D\u5FC3\u5316\u91D1\u878D\u751F\u6001\u3002 DeFi\u7684\u521B\u65B0\u4E0D\u65AD\u62D3\u5BBD\u4E86\u52A0\u5BC6\u884C\u4E1A\u5728\u91D1\u878D\u9886\u57DF\u7684\u5E94\u7528\uFF0C\u4FC3\u8FDB\u4E86\u53BB\u4E2D\u5FC3\u5316\u91D1\u878D\u7684\u5FEB\u901F\u53D1\u5C55\u3002\u65E0\u8BBA\u662F\u53BB\u4E2D\u5FC3\u5316\u4EA4\u6613\u3001\u501F\u8D37\uFF0C\u8FD8\u662F\u5404\u79CD\u91D1\u878D\u884D\u751F\u54C1\uFF0C\u968F\u7740\u7528\u6237\u91CF\u7684\u6FC0\u589E\u548C\u5E02\u573A\u7684\u4E0D\u65AD\u6269\u5927\uFF0CDeFi\u5DF2\u9010\u6E10\u88AB\u8BA4\u53EF\u3002DeBank\u6570\u636E\u663E\u793A\uFF0CDeFi\u9886\u57DF\u7684\u603B\u9501\u4ED3\u91CF\u5DF2\u8D85\u8FC71100\u4EBF\u7F8E\u5143\u3002</li></ul><h3 id="_2-\u3001-\u53BB\u4E2D\u5FC3\u5316\u7684\u56DB\u79CD\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#_2-\u3001-\u53BB\u4E2D\u5FC3\u5316\u7684\u56DB\u79CD\u65B9\u5F0F" aria-hidden="true">#</a> 2)\u3001 \u53BB\u4E2D\u5FC3\u5316\u7684\u56DB\u79CD\u65B9\u5F0F</h3><h4 id="\u53BB\u4E2D\u5FC3\u5316\u4E3B\u8981\u5305\u62EC\u56DB\u79CD\u65B9\u5F0F" tabindex="-1"><a class="header-anchor" href="#\u53BB\u4E2D\u5FC3\u5316\u4E3B\u8981\u5305\u62EC\u56DB\u79CD\u65B9\u5F0F" aria-hidden="true">#</a> \u53BB\u4E2D\u5FC3\u5316\u4E3B\u8981\u5305\u62EC\u56DB\u79CD\u65B9\u5F0F\uFF1A</h4><ul><li>\u7B2C\u4E00\u79CD\u65B9\u5F0F\u662F\u8D85\u989D\u62B5\u62BC\uFF1B</li><li>\u7B2C\u4E8C\u79CD\u662F\u8DE8\u94FE\uFF0C\u6BD4\u5982\u8BF4\u6BD4\u7279\u5E01\u5982\u4F55\u5728\u4EE5\u592A\u574A\u4E0A\u9762\u53D8\u6210\u4E00\u4E2AERC20\u7684token\uFF0C\u4E3B\u8981\u662F\u5728\u6BD4\u7279\u5E01\u7684\u7F51\u7EDC\u65B9\u9762\u5EFA\u7ACB\u4E00\u79CD\u7C7B\u4F3C\u4E8Evolt\u50A8\u85CF\u67DC\u7684\u673A\u5236\uFF0C\u6765\u5B9E\u73B0\u5B83\u7684\u4E00\u5BF9\u4E00\u6620\u5C04\uFF1B</li><li>\u7B2C\u4E09\u79CD\u662F\u5F39\u6027\u7B97\u6CD5\u7A33\u5B9A\u5E01\uFF0C\u5728\u8FD9\u79CD\u65B9\u5F0F\u4E0B\uFF0C\u5F53\u5E01\u503C\u4E0E\u7F8E\u5143\u7684\u6C47\u7387\u53D1\u751F\u6539\u53D8\u65F6\uFF0C\u5B83\u8981\u6709\u4E00\u5B9A\u7684\u673A\u5236\uFF08\u589E\u53D1\u6216\u9500\u6BC1\uFF09\u6765\u7A33\u5B9A\u5B83\u7684\u5E01\u503C\uFF0C\u4F46\u662F\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u5F53\u4E2D\uFF0C\u5176\u5B9E\u8FD8\u9700\u8981\u6709\u5916\u90E8\u7684\u6570\u636E\u8F93\u5165\uFF1B</li><li>\u7B2C\u56DB\u79CD\u662F\u90E8\u5206\u50A8\u5907\u7A33\u5B9A\u5E01\uFF0C\u76EE\u524D\u89C4\u6A21\u6BD4\u8F83\u5C0F\u3002</li></ul><h3 id="_3-\u3001defi\u5178\u578B\u7684\u5E94\u7528" tabindex="-1"><a class="header-anchor" href="#_3-\u3001defi\u5178\u578B\u7684\u5E94\u7528" aria-hidden="true">#</a> 3)\u3001Defi\u5178\u578B\u7684\u5E94\u7528</h3><p><img src="'+n+'" alt=""></p><ul><li>\u2460 DeFi\u5E94\u7528\u2014\u501F\u8D37\uFF08\u62B5\u62BC\u6570\u5B57\u8D44\u4EA7\u8D37\u51FA\u6570\u5B57\u8D44\u4EA7\uFF09 \u4F20\u7EDF\u91D1\u878D\u7CFB\u7EDF\u7684\u501F\u8D37\u4F53\u7CFB\u9700\u8981\u501F\u6B3E\u4EBA\u6709\u826F\u597D\u7684\u4FE1\u8A89\u548C\u94F6\u884C\u4F5C\u4E3A\u4E2D\u4ECB\uFF0C\u53BB\u4E2D\u5FC3\u5316\u501F\u8D37\u5219\u6D88\u9664\u4E86\u8FD9\u4E9B\u58C1\u5792\u3002\u4EFB\u4F55\u4EBA\u90FD\u53EF\u4EE5\u5728\u94FE\u4E0A\u7684\u501F\u8D37\u9879\u76EE\u5E73\u53F0\u62B5\u62BC\u5176\u6570\u5B57\u8D44\u4EA7\uFF0C\u62B5\u62BC\u54C1\u662F\u552F\u4E00\u7684\u5BA1\u6838\u6807\u51C6\uFF0C\u507F\u8FD8\u8D37\u6B3E\u7684\u5229\u7387\u7531\u667A\u80FD\u5408\u7EA6\u7B97\u6CD5\uFF0C\u6839\u636E\u5E02\u573A\u4F9B\u9700\u800C\u52A8\u6001\u8BA1\u7B97\u3002\u653E\u8D37\u4E5F\u4E0D\u518D\u662F\u4E2D\u5FC3\u5316\u6743\u5A01\u673A\u6784\u7684\u6743\u5229\uFF0C\u4EFB\u4F55\u4EBA\u90FD\u53EF\u4EE5\u5C06\u5176\u6570\u5B57\u8D44\u4EA7\u6CE8\u5165\u501F\u8D37\u6C60\u8D5A\u53D6\u5229\u606F\uFF0C\u4ECE\u8D44\u4EA7\u4E2D\u83B7\u5F97\u6536\u76CA\u3002\u8003\u8651\u5230\u5E01\u4EF7\u4E0B\u8DCC\u7684\u98CE\u9669\uFF0C\u76EE\u524DDeFi\u501F\u8D37\u5E73\u53F0\uFF0CBTC\u3001ETH\u7684\u8D28\u62BC\u7387\u572860-80%\u4E4B\u95F4\u3002</li><li>\u2461 DeFi\u5E94\u7528\u2014\u53BB\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240\uFF08DecentralizedExchange\uFF09 \u53BB\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240\uFF0C\u5168\u79F0DecentralizedExchange\uFF0C\u6307\u7684\u662F\u91C7\u7528\u667A\u80FD\u5408\u7EA6\u8FD0\u884C\u5728\u533A\u5757\u94FE\u7F51\u7EDC\u4E0A\u7684\u4EA4\u6613\u6240\u3002\u4E0E\u4F20\u7EDF\u7684\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240\u76F8\u6BD4\uFF0C\u53BB\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240\u6709\u4EE5\u4E0B\u51E0\u4E2A\u7279\u70B9\uFF1A <ul><li>\u4E2D\u7ACB\u4E14\u65E0\u9700\u505A\u5E02\uFF1ADex\u81EA\u8EAB\u4E0D\u63D0\u4F9B\u505A\u5E02\u6240\u9700\u6570\u5B57\u8D44\u4EA7\uFF0C\u53EA\u63D0\u4F9B\u505A\u5E02\u7684\u667A\u80FD\u5408\u7EA6\u7B97\u6CD5\u3002\u7528\u6237\u4E3A\u5E73\u53F0\u63D0\u4F9B\u6D41\u52A8\u6027\u53EF\u4EE5\u5F97\u5230\u5E73\u53F0\u4EA4\u6613\u624B\u7EED\u8D39\u7684\u5206\u6210\u4EE5\u53CA\u9879\u76EE\u65B9\u7684\u6FC0\u52B1\u3002\u4EE5cake\u4E3A\u4F8B\uFF0C\u6BCF\u7B14\u4EA4\u6613\u6536\u53D6\u53432\u624B\u7EED\u8D39\uFF0C\u5176\u4E2D\u53431.7\u53D1\u653E\u7ED9\u505A\u5E02\u5546\uFF0C\u4E073\u4F5C\u4E3A\u5E73\u53F0\u76C8\u4F59\u516C\u79EF\u3002\u53D1\u751F\u4EA4\u6613\u65F6\uFF0C\u8D44\u4EA7\u76F4\u63A5\u5728\u7528\u6237\u53CC\u65B9\u7684\u533A\u5757\u94FE\u94B1\u5305\u91CC\u8FDB\u884C\u4EA4\u6362\uFF0C\u800C\u4E0D\u7ECF\u8FC7\u7B2C\u4E09\u65B9\u4EA4\u6536\u673A\u6784\u3002 \u65E0\u9700KYC\uFF1A\u53BB\u4E2D\u5FC3\u5316\u4EA4\u6613\u6240\u65E0\u9700\u6CE8\u518C\u3001\u5B9E\u540D\u7B49\u4E00\u7CFB\u5217\u64CD\u4F5C\uFF0C\u4EC5\u9700\u4E00\u4E2A\u533A\u5757\u94FE\u94B1\u5305\u5373\u53EF\u8FDB\u884C\u64CD\u4F5C\u3002 \u4E0A\u5E01\u95E8\u69DB\u4F4E\uFF1A\u4EFB\u4F55\u4EBA\u53EF\u4EE5\u65E0\u95E8\u69DB\u7684\u53D1\u884C\u81EA\u5DF1\u7684\u52A0\u5BC6\u8D27\u5E01\uFF0C\u8FD9\u79CD\u514D\u5BA1\u6838\u673A\u5236\u4E3A\u8BB8\u591A\u4E2D\u5C0F\u9879\u76EE\u5E26\u6765\u4E86\u8BF8\u591A\u521B\u65B0\u673A\u4F1A\uFF0C\u540C\u65F6\u4E5F\u9700\u63D0\u9632\u7A7A\u6C14\u5E01\u7684\u5B58\u5728\u3002</li></ul></li><li>\u2462 DeFi\u5E94\u7528\u2014\u53BB\u4E2D\u5FC3\u5316\u884D\u751F\u54C1\uFF08\u5408\u6210\u8D44\u4EA7\u6848\u4F8B\uFF09 \u884D\u751F\u54C1\u662F\u4E00\u79CD\u4EF7\u503C\u6765\u6E90\u4E8E\u80A1\u7968\u3001\u5546\u54C1\u3001\u8D27\u5E01\u3001\u6307\u6570\u3001\u503A\u5238\u6216\u5229\u7387\u7B49\u5176\u5B83\u6807\u7684\u8D44\u4EA7\u7684\u5408\u7EA6\u3002\u884D\u751F\u54C1\u662F\u6240\u6709\u6210\u719F\u7684\u91D1\u878D\u7CFB\u7EDF\u7684\u5173\u952E\u8981\u7D20\u4E4B\u4E00\u3002\u884D\u751F\u54C1\u6709\u4E24\u4E2A\u4E3B\u8981\u7528\u4F8B\uFF1A\u5957\u671F\u4FDD\u503C\u548C\u6295\u673A\u3002</li><li>\u2463 DeFi\u5E94\u7528\u2014\u53BB\u4E2D\u5FC3\u5316\u4FDD\u9669 \u7531\u4E8EDeFi\u751F\u6001\u91CC\u6F5C\u5728\u7684\u5DE8\u989D\u652F\u51FA\u573A\u666F\u7684\u5B58\u5728\uFF0C\u8D28\u62BC\u5728\u667A\u80FD\u5408\u7EA6\u4E2D\u7684\u4EE3\u5E01\u5BB9\u6613\u53D7\u5230\u5B89\u5168\u6027\u653B\u51FB\u3002\u5C3D\u7BA1\u5927\u591A\u6570\u9879\u76EE\u7684\u667A\u80FD\u5408\u7EA6\u90FD\u8FDB\u884C\u4E86\u4EE3\u7801\u5BA1\u8BA1\uFF0C\u88AB\u9ED1\u5BA2\u5165\u4FB5\u7684\u53EF\u80FD\u6027\u603B\u662F\u5B58\u5728\uFF0C\u8FD9\u5C06\u5BFC\u81F4\u8D44\u91D1\u635F\u5931\u3002\u4F8B\u5982\uFF0CbZx\u57282020\u5E74\u4E0A\u53D1\u751F\u4E86\u4E09\u8D77\u5F15\u4EBA\u6CE8\u76EE\u7684DeFi\u5B89\u5168\u653B\u51FB\u4E8B\u4EF6\u3002\u8FD9\u4E09\u6B21\u653B\u51FB\u53D1\u751F\u65F6\u95F4\u4E3A2020\u5E742\u670815\u65E5\u300118\u65E5\u548C2020\u5E749\u670815\u65E5\uFF0C\u603B\u635F\u5931\u4EF7\u503C\u7EA6900\u4E07\u7F8E\u5143\u3002</li><li>\u2464 DeFi\u5E94\u7528\u2014\u673A\u67AA\u6C60\u3001\u5B9E\u73B0\u8D44\u4EA7\u6536\u76CA\u6700\u5927\u5316 \u673A\u67AA\u6C60\uFF0C\u672C\u8D28\u4E0A\u662F\u4E00\u4E2A\u5B9E\u73B0\u8D44\u4EA7\u6536\u76CA\u6700\u5927\u5316\u7B56\u7565\u7684\u8D44\u91D1\u6C60\uFF0C\u81EA\u52A8\u5316\u6267\u884C\u6D41\u52A8\u6027\u6316\u77FF\u3002DeFi\u673A\u67AA\u6C60\u4F1A\u6839\u636E\u5B9E\u65F6\u6316\u77FF\u6536\u76CA\u7684\u9AD8\u4F4E\uFF0C\u5C06\u8D44\u91D1\u5207\u6362\u81F3\u66F4\u9AD8\u6536\u76CA\u7684DeFi\u9879\u76EE\u4E2D\u8FDB\u884C\u6D41\u52A8\u6027\u6316\u77FF\uFF0C\u4E3A\u6295\u8D44\u8005\u63D0\u4F9B\u66F4\u9AD8\u7684\u6316\u77FF\u6536\u76CA\uFF0C\u5176\u672C\u8D28\u4E0A\u5C31\u662F\u4E00\u79CD\u901A\u8FC7\u667A\u80FD\u8C03\u5EA6\u8FBE\u5230\u6700\u4F18\u6536\u76CA\u7684\u7B56\u7565\u77FF\u6C60\u3002 \u673A\u67AA\u6C60\u5E94\u7528\u4E3E\u4F8B\uFF1A <ul><li>1.Yearn.finance\uFF08YFI\uFF09\u662F\u5EFA\u7ACB\u5728\u4EE5\u592A\u574A\u516C\u94FE\u4E0A\u7684\u673A\u67AA\u6C60\u3002YFI\u4E0A\u7684\u6BCF\u4E2A\u7B56\u7565\u90FD\u662F\u7531\u793E\u533A\u9009\u62E9\uFF0C\u673A\u67AA\u6C60\u91CC\u76C8\u5229\u7684\u4EA4\u6613\u4F1A\u4EA7\u751F5%\u7684\u8D39\u7528\u6765\u8865\u8D34\u673A\u67AA\u6C60\u7684Gas\u8D39\u6210\u672C\uFF0C\u540C\u65F6\u4F1A\u5C06\u8FD9\u90E8\u5206\u8D39\u7528\u768410%\u5956\u52B1\u7ED9\u7B56\u7565\u521B\u5EFA\u8005\u3002</li><li>2.Pancake.bunny\uFF08Bunny\uFF09\u662F\u5EFA\u7ACB\u5728\u5E01\u5B89\u667A\u80FD\u94FE\u4E0A\u7684\u673A\u67AA\u6C60\u3002</li></ul></li></ul><h3 id="_4-\u3001-nft\u5E02\u573A\u7684\u7E41\u8363" tabindex="-1"><a class="header-anchor" href="#_4-\u3001-nft\u5E02\u573A\u7684\u7E41\u8363" aria-hidden="true">#</a> 4)\u3001 NFT\u5E02\u573A\u7684\u7E41\u8363</h3><ul><li><p>1\uFF09NFT\u7684\u7279\u70B9\u2022\u6807\u8BB0\u7279\u5B9A\u8D44\u4EA7\u7684\u6240\u6709\u6743 NFT\u5229\u7528\u533A\u5757\u94FE\u6280\u672F\u6807\u8BB0\u4E86\u7528\u6237\u5BF9\u4E8E\u7279\u5B9A\u8D44\u4EA7\u7684\u6240\u6709\u6743\uFF0C\u6210\u4E3A\u8BE5\u7279\u5B9A\u8D44\u4EA7\u516C\u8BA4\u7684\u53EF\u4EA4\u6613\u6027\u5B9E\u4F53\uFF0C\u540C\u65F6NFT\u7684\u4EF7\u683C\u53CD\u5E94\u4E86\u5E02\u573A\u5BF9\u4E8E\u5176\u6620\u5C04\u7684\u8D44\u4EA7\u4EF7\u503C\u548C\u7A00\u7F3A\u6027\u7684\u8BA4\u53EF\u3002</p><ul><li>\u771F\u5B9E\u6027\u4E0E\u552F\u4E00\u6027\uFF1ANFT\u51ED\u501F\u533A\u5757\u94FE\u6280\u672F\u4E0D\u53EF\u7BE1\u6539\u3001\u8BB0\u5F55\u53EF\u8FFD\u6EAF\u7B49\u7279\u70B9\u8BB0\u5F55\u4EA7\u6743\uFF0C\u5E76\u786E\u4FDD\u771F\u5B9E\u6027\u548C\u552F\u4E00\u6027\u3002NFT\u53EF\u4EE5\u7528\u6765\u4EE3\u8868\u5404\u79CD\u8D44\u4EA7\uFF0C\u5982\u865A\u62DF\u6536\u85CF\u54C1\u3001\u6E38\u620F\u5185\u8D44\u4EA7\u3001\u865A\u62DF\u8D44\u4EA7\u3001\u6570\u5B57\u827A\u672F\u54C1\u3001\u623F\u5730\u4EA7\u7B49\u3002\u2022\u951A\u5B9A\u975E\u540C\u8D28\u5316\u8D44\u4EA7\u4EF7\u503C\uFF1A\u76F8\u6BD4\u4E8E\u540C\u8D28\u5316\u4EE3\u5E01(\u5982\u73B0\u5B9E\u8D27\u5E01\u3001\u865A\u62DF\u8D27\u5E01)\uFF0CNFT\u4E0E\u5176\u672C\u8D28\u4E0A\u7684\u5DEE\u5F02\u662FNFT\u951A\u5B9A\u7684\u662F\u975E\u540C\u8D28\u5316\u8D44\u4EA7\u7684\u4EF7\u503C\u3002FT\u951A\u5B9A\u7684\u662F\u540C\u8D28\u5316\u7684\u8D44\u4EA7\u5982\u9EC4\u91D1\u3001\u7F8E\u5143\u7B49\u3002\u4E8C\u8005\u90FD\u5177\u6709\u53EF\u4EA4\u6613\u5C5E\u6027\uFF0C\u76F8\u540C\u7684FT\u4EF7\u503C\u662F\u53EF\u4E92\u6362\u7684\uFF0C\u4F46\u662F\u6BCF\u4E00\u679ANFT\u6240\u5BF9\u5E94\u7684\u4EF7\u503C\u662F\u72EC\u4E00\u65E0\u4E8C\u7684\u3002</li></ul></li><li><p>2\uFF09\u540C\u8D28\u5316\u4EE3\u5E01(FT)\u4E0E\u975E\u540C\u8D28\u5316\u4EE3\u5E01(NFT)\u5BF9\u6BD4\u2022\u540C\u8D28\u5316\u4EE3\u5E01(FT) \u6240\u8C13\u540C\u8D28\u5316\uFF0C\u5C31\u662F\u6307\u8D44\u4EA7\u4E4B\u95F4\u9075\u5FAA\u7740\u76F8\u540C\u7684\u89C4\u5219\uFF0C\u5E76\u4E14\u53EF\u4EE5\u4EA4\u6613\u7F6E\u6362\u548C\u81EA\u7531\u5206\u5272\u3002\u5982\u52A0\u5BC6\u6570\u5B57\u8D27\u5E01\u6BD4\u7279\u5E01\uFF0C\u5728\u540C\u4E00\u4E2A\u65F6\u95F4\u70B9\uFF0C\u6BCF\u4E00\u679A\u6BD4\u7279\u5E01\u5BF9\u5E94\u7684\u4EF7\u683C\u662F\u76F8\u540C\u7684\uFF0C\u4E00\u4E2A\u6BD4\u7279\u5E01\u53EF\u4EE5\u7B49\u4EF7\u4EA4\u6362\u53E6\u4E00\u4E2A\u6BD4\u7279\u5E01\uFF0C\u540C\u65F6\u6BD4\u7279\u5E01\u4E5F\u53EF\u4EE5\u5206\u5272\u62100\u4E040.01\u62160.0001 \u679A\u6BD4\u7279\u5E01\u3002</p><ul><li>\u975E\u540C\u8D28\u5316\u4EE3\u5E01(NFT):\u975E\u540C\u8D28\u5316\u610F\u5473\u7740\u5B8C\u5168\u72EC\u7279\u4E14\u552F\u4E00\uFF0C\u5E76\u4E14\u4E0D\u80FD\u5206\u5272\u3001\u5F7C\u6B64\u4E4B\u95F4\u4E0D\u80FD\u81EA\u7531\u4EA4\u6362\uFF0C\u6BD4\u5982\u751F\u6D3B\u4E2D\u5E38\u89C1\u7684\u623F\u4EA7\u3001\u6C7D\u8F66\u3001\u62A4\u7167\u7B49\u3002\u4EFB\u610F2\u680B\u522B\u5885\u6237\u578B\u3001\u4EF7\u683C\u3001\u5F00\u53D1\u5546\u3001\u4F4D\u7F6E\u3001\u7269\u4E1A\u3001\u9762\u79EF\u7B49\u662F\u4E0D\u4E00\u6837\u7684\uFF0C\u4E3B\u4EBA\u4E5F\u4E0D\u4E00\u6837\uFF0C\u800C\u4E14\u4E00\u680B\u522B\u5885\u4E0D\u80FD\u62C6\u5206\u6210\u5F88\u591A\u4EFD\u5356\u7ED9\u5F88\u591A\u4EBA\u3002</li></ul></li></ul><h3 id="_5-\u3001burydao\u7684\u8BDE\u751F" tabindex="-1"><a class="header-anchor" href="#_5-\u3001burydao\u7684\u8BDE\u751F" aria-hidden="true">#</a> 5)\u3001BuryDAO\u7684\u8BDE\u751F</h3><p>\u81EA1972\u5E74\u7B2C\u4E00\u5BB6\u5E97\u94FA\u5F00\u542F\u4EE5\u6765\uFF0CJaspal\u4E00\u76F4\u5C31\u662F\u6CF0\u56FD\u65F6\u5C1A\u7684\u524D\u6CBF\u3002\u867D\u7136\u662F\u6CF0\u56FD\u672C\u571F\u7684\u54C1\u724C\uFF0C\u5374\u5F88\u6709\u6B27\u6D32\u54C1\u724C\u7684\u67B6\u5F0F\uFF0C\u5728\u6CF0\u56FD\u76F8\u5F53\u6709\u77E5\u540D\u5EA6\uFF0C\u5404\u5927\u767E\u8D27\u516C\u53F8\u51E0\u4E4E\u90FD\u6709\u8BBE\u6709\u4E13\u67DC\u3002\u6CF0\u56FD\u5168\u56FD\u670939\u5BB6\u95E8\u5E97\uFF0C\u5176\u4E2D\u8BB8\u591A\u95E8\u5E97\u5728\u66FC\u8C37\uFF0C\u73B0\u5728\u6700\u5927\u7684\u95E8\u5E97\u5728\u66B9\u7F57\u4E2D\u5FC3\u548C\u4E2D\u592E\u4E16\u754C\u5E7F\u573A\u91CC\u3002</p><p>Jaspal\u73B0\u5728\u7684\u8BBE\u8BA1\u5E08\u662F\u7EBD\u7EA6\u8BBE\u8BA1\u5708\u4EAB\u6709\u76DB\u540D\u7684\u6CF0\u88D4\u8BBE\u8BA1\u5E08NujNovakhet\uFF0C\u4E0D\u5C11\u6CF0\u56FD\u4EBA\u5BF9\u6B64\u611F\u5230\u9A84\u50B2\u3002Jaspal\u4E3B\u6253\u9AD8\u7AEF\u65F6\u5C1A\u98CE\uFF0C\u670D\u9970\u90FD\u5145\u6EE1\u4E86\u827A\u672F\u7684\u6C14\u606F\u3002Nuj Novakhet\u5BF9\u4E8E\u670D\u88C5\u7684\u56FE\u6848\u5341\u5206\u8BB2\u7A76\uFF0C\u9664\u4E86\u5E38\u89C1\u7684\u8C79\u7EB9\u5916\uFF0C\u8FD8\u6709\u4E00\u4E9B\u72EC\u5177\u4E2A\u6027\u7684\u4E0D\u89C4\u5219\u51E0\u4F55\u56FE\u6848\u3002\u4ED6\u5584\u4E8E\u628A\u827A\u672F\u4E0E\u65F6\u5C1A\u76F8\u7ED3\u5408\uFF0C\u521B\u9020\u51FA\u4E0E\u4F17\u4E0D\u540C\u7684\u65F6\u5C1A\u3002Jaspal\u7684\u8863\u670D\u9762\u6599\u548C\u6B3E\u5F0F\u90FD\u5F88\u4E0D\u9519,\u649E\u886B\u7684\u673A\u7387\u57FA\u672C\u4E3A0\uFF0C\u56DE\u5934\u7387\u4E5F\u633A\u9AD8\u7684\u3002</p><p>Jaspal\u7D27\u8DDF\u56DB\u5B63\u6F6E\u6D41\uFF0C\u5C31\u7B97\u66FC\u8C3711\u6708\u4EFD\u8FD8\u662F\u4E09\u5341\u591A\u5EA6\u7684\u5929\u6C14\u91CC\uFF0C\u8FD9\u5B63\u7684\u8863\u670D\u4ECD\u7136\u548C\u5BD2\u51B7\u56FD\u5BB6\u540C\u6B65\uFF0C\u51FA\u552E\u5047\u76AE\u8349\u7B49\u4FDD\u6696\u6027\u7684\u65F6\u5C1A\u670D\u9970\u3002\u5BF9\u4E8E\u6765\u65C5\u6E38\u7684\u4EBA\u6765\u8BF4\uFF0C\u5B8C\u5168\u53EF\u4EE5\u4E70\u56DE\u5BB6\u7A7F\u3002\u670D\u88C5\u7684\u8D28\u91CF\u90FD\u5F88\u4E0D\u9519\uFF0C\u6BCF\u4EF6\u8863\u670D\u90FD\u6709\u76F8\u5E94\u7684\u54C1\u8D28\u3002\u9664\u4E86\u670D\u9970\u5916\uFF0CJaspal \u4E5F\u5728\u8BBE\u8BA1\u5973\u5305\u3001\u914D\u9970\u7B49\u3002</p><p>Jaspal\u4ECE\u5BB6\u5177\u8D77\u4E1A\uFF0C\u5230\u6CF0\u56FD\u4E00\u7EBF\u670D\u9970\u54C1\u724C\u3002\u4F5C\u4E3A\u6CF0\u56FD\u591A\u5143\u5316\u4EA7\u54C1\u7684\u96C6\u56E2\uFF0CJaspal\u65D7\u4E0B\u9664\u4E86\u5BB6\u5C45\u548C\u623F\u5730\u4EA7\u4EA7\u4E1A\u5916\uFF0C\u8FD8\u5305\u62EC\u591A\u4E2A\u670D\u9970\u54C1\u724C\uFF0C\u5305\u62ECJaspal\u3001CPS\u3001CC DoubleO\u3001LynAround\u548CLyn\uFF0C\u63D0\u4F9B\u4E0D\u540C\u98CE\u683C\u7684\u6210\u8863\u6765\u8FCE\u5408\u4E0D\u540C\u53E3\u5473\u7684\u987E\u5BA2\u3002\u800C\u4E3B\u54C1\u724CJaspal\u4E3B\u8981\u5438\u5F15\u7684\u662F\u8FFD\u6C42\u54C1\u8D28\u548C\u65F6\u5C1A\u6B3E\u5F0F\u7684\u987E\u5BA2\u3002\u800CBuryDAO\u4F5C\u4E3AJaspal\u65D7\u4E0B\u7684\u65F6\u5C1A\u6F6E\u724C\uFF0C\u7B2C\u4E00\u4E2A\u6F6E\u724C\u548C\u533A\u5757\u94FE\u6316\u77FF\u9879\u76EE\u7684\u7ED3\u5408\uFF0C\u7B2C\u4E00\u671F\u5C06\u9650\u91CF\u53D1\u884C\u6F6E\u724C\u54C1\u724C\u7EAA\u5FF5\u5361</p><p><img src="'+d+'" alt=""></p>',20),t=[s];function p(c,u){return e(),i("div",null,t)}var o=a(h,[["render",p],["__file","index.html.vue"]]);export{o as default};
